import React from 'react';
import ReactDOM from 'react-dom';
import Main from './Main';
import './css/index.css';
import { createStore } from 'redux';
import { Provider } from 'react-redux';

const initialState = {
	favo: [],
	initItems: [],
};

function reducer(state = initialState, action){

	switch(action.type) {
		case "INIT":
			return{
				...state,
				initItems: state.initItems.concat(action.initItems)
			}
		case "SYNC":
			return {
				...state,
				favo: state.favo.concat(action.favo)
			}
		case "COMPARE":

		// HITTA ALLA FAVORITER I FAVO OCH FÄRGLÄGG INITITEMS, SEN TABORT FUNKTION FÖR FAVO
		var copyOfInit = state.initItems;

		  for (var i = 0; i < state.favo.length; i++) {
		    console.log(state.favo[i].id);
		    for (var j = 0; j < state.initItems.length; j++) {
		        if (state.favo[i].id == state.initItems[j].id) {
		        	console.log(state.initItems[j].id);
		          console.log("MATCH");
		          copyOfInit[j].isFavorite = true;
		          console.log(copyOfInit);
		        }
		    }
		  }

			return {
				...state,
				initItems: copyOfInit
			}

		case "ADDFAV":
			return state;
		case "REMOVEFAV":
			return state;
		default:
			return state;
	}

}

const store = createStore(reducer);

store.subscribe(() => {
console.log("STore", store.getState());	
})



const App = () => (
<Provider store={store}>
	<Main />
</Provider>

);

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
