import React from 'react';
import ReactDOM from 'react-dom';
import Main from './Main';
import './css/index.css';
import { createStore } from 'redux';
import { Provider } from 'react-redux';

const initialState = {
	favo: [],
	initItems: [],
	pressedId: 0,
};

function reducer(state = initialState, action){

	switch(action.type) {
		case "INIT":
			return{
				...state,
				initItems: state.initItems.concat(action.initItems)
			}
		case "SYNC":

		var copyOfInit = state.initItems;

		  for (var i = 0; i < action.favo.length; i++) {
		    console.log(action.favo.id);
		    for (var j = 0; j < state.initItems.length; j++) {
		        if (action.favo[i].id == state.initItems[j].id) {
		        	console.log(state.initItems[j].id);
		          console.log("MATCH");
		          copyOfInit[j].isFavorite = true;
		          console.log(copyOfInit);
		        }
		    }
		  }


			return {
				...state,
				favo: state.favo.concat(action.favo)
			}
		/*case "COMPARE":

		// HITTA ALLA FAVORITER I FAVO OCH FÄRGLÄGG INITITEMS, SEN TABORT FUNKTION FÖR FAVO
		var copyOfInit = state.initItems;

		  for (var i = 0; i < state.favo.length; i++) {
		    console.log(state.favo[i].id);
		    for (var j = 0; j < state.initItems.length; j++) {
		        if (state.favo[i].id == state.initItems[j].id) {
		        	console.log(state.initItems[j].id);
		          console.log("MATCH");
		          copyOfInit[j].isFavorite = true;
		          console.log(copyOfInit);
		        }
		    }
		  }

			return {
				...state,
				initItems: copyOfInit
			}*/

		case "ADDFAV":
			return state;
		case "REMOVEFAV":

		var oldFavo = state.favo;

		//sätt isFavorite = false på den index

		for (var i = 0; i < oldFavo.length; i++) {
			if(action.pressedId == oldFavo[i].id){
				oldFavo[i].isFavorite = false;
			}
		}

		for (var i = 0; i < initItems.length; i++) {
			if(action.pressedId == initItems[i].id){
				initItems[i].isFavorite = false;
			}
		}

		//Delete i två for-loopar (en för fav (TA BORT OBJ), en för items (ISFAVORITE = FALSE)
		//, returna båda nya 


			return {
				...state,
				favo: oldFavo
			}
		default:
			return state;
	}

}

const store = createStore(reducer);

store.subscribe(() => {
console.log("STore", store.getState());	
})



const App = () => (
<Provider store={store}>
	<Main />
</Provider>

);

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
